import React, { useState } from "react";

export default function Dashboard({ onLogout }) {
  const [transactions, setTransactions] = useState([
    { id: 1, tipo: "ingreso", monto: 1000, fecha: "2025-09-15" },
    { id: 2, tipo: "gasto", monto: 200, fecha: "2025-09-14" },
  ]);

  const [tipo, setTipo] = useState("ingreso");
  const [monto, setMonto] = useState("");
  const [fecha, setFecha] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const addTransaction = () => {
    if (!monto || !fecha) return;
    const newTransaction = { id: Date.now(), tipo, monto: parseFloat(monto), fecha };
    setTransactions([...transactions, newTransaction]);
    setMonto(""); setFecha("");
  };

  const askAssistant = () => {
    if (!question) return;
    setAnswer("Respuesta simulada del asistente: ¡Hola!");
  };

  return (
    <div className="container">
      <div style={{display:"flex", justifyContent:"space-between", alignItems:"center"}}>
        <h2>Dashboard</h2>
        <button className="danger" onClick={onLogout}>Logout</button>
      </div>

      <h3>Agregar Transacción</h3>
      <select value={tipo} onChange={e=>setTipo(e.target.value)}>
        <option value="ingreso">Ingreso</option>
        <option value="gasto">Gasto</option>
      </select>
      <input type="number" placeholder="Monto" value={monto} onChange={e=>setMonto(e.target.value)} />
      <input type="date" value={fecha} onChange={e=>setFecha(e.target.value)} />
      <button className="primary" onClick={addTransaction}>Agregar</button>

      <h3>Transacciones</h3>
      <table>
        <thead>
          <tr><th>Tipo</th><th>Monto</th><th>Fecha</th></tr>
        </thead>
        <tbody>
          {transactions.map(t => (
            <tr key={t.id}>
              <td>{t.tipo}</td>
              <td>{t.monto}</td>
              <td>{t.fecha}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Asistente IA</h3>
      <textarea value={question} onChange={e=>setQuestion(e.target.value)} placeholder="Pregunta..."></textarea>
      <button className="secondary" onClick={askAssistant}>Preguntar</button>
      {answer && <div style={{marginTop:"10px", padding:"10px", border:"1px solid #ccc"}}><strong>Respuesta:</strong> {answer}</div>}
    </div>
  );
}
