import { useEffect, useState } from "react";
import api from ".."; // ahora apunta dentro de src/
console.log("Token:", token);
console.log("Transactions:", transactions);

export default function Dashboard({ onLogout }) {
  const [transactions, setTransactions] = useState([]);
  const [tipo, setTipo] = useState("ingreso");
  const [monto, setMonto] = useState("");
  const [fecha, setFecha] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const token = localStorage.getItem("token");

  const fetchTransactions = async () => {
    try {
      const res = await api.get("/transactions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    if (token) fetchTransactions();
  }, [token]);

  const addTransaction = async () => {
    if (!monto || !fecha) return;
    try {
      await api.post(
        "/transactions",
        { tipo, monto: parseFloat(monto), fecha },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMonto("");
      setFecha("");
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  const askAssistant = async () => {
    if (!question) return;
    try {
      const res = await api.post(
        "/assistant/query",
        { question },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAnswer(res.data.answer);
    } catch (err) {
      console.error(err);
      setAnswer("Error al comunicarse con el asistente");
    }
  };

  return (
    <div className="max-w-3xl mx-auto mt-5 p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-bold">Dashboard</h2>
        <button onClick={onLogout} className="bg-red-500 text-white px-3 py-1 rounded">
          Logout
        </button>
      </div>

      {/* Formulario para agregar transacción */}
      <div className="mb-4 p-4 border rounded">
        <h3 className="font-bold mb-2">Agregar Transacción</h3>
        <select value={tipo} onChange={(e) => setTipo(e.target.value)} className="border p-1 mr-2">
          <option value="ingreso">Ingreso</option>
          <option value="gasto">Gasto</option>
        </select>
        <input
          type="number"
          placeholder="Monto"
          value={monto}
          onChange={(e) => setMonto(e.target.value)}
          className="border p-1 mr-2"
        />
        <input
          type="date"
          value={fecha}
          onChange={(e) => setFecha(e.target.value)}
          className="border p-1 mr-2"
        />
        <button onClick={addTransaction} className="bg-blue-500 text-white px-2 py-1 rounded">
          Agregar
        </button>
      </div>

      {/* Tabla de transacciones */}
      <div className="mb-4">
        <h3 className="font-bold mb-2">Transacciones</h3>
        <table className="w-full border">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-2 py-1">Tipo</th>
              <th className="border px-2 py-1">Monto</th>
              <th className="border px-2 py-1">Fecha</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t) => (
              <tr key={t.id}>
                <td className="border px-2 py-1">{t.tipo}</td>
                <td className="border px-2 py-1">{t.monto}</td>
                <td className="border px-2 py-1">{t.fecha}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Asistente IA */}
      <div className="mb-4 p-4 border rounded">
        <h3 className="font-bold mb-2">Asistente IA</h3>
        <textarea
          placeholder="Escribe tu pregunta..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          className="w-full border p-2 mb-2"
        />
        <button onClick={askAssistant} className="bg-purple-500 text-white px-2 py-1 rounded">
          Preguntar
        </button>
        {answer && (
          <div className="mt-2 p-2 border rounded bg-gray-100">
            <strong>Respuesta:</strong>
            <p>{answer}</p>
          </div>
        )}
      </div>
    </div>
  );
}
