import axios from "axios";

const API_URL = "http://localhost:3001"; // Cambia al URL de tu backend si es necesario

const api = axios.create({
  baseURL: API_URL,
});

// --- AUTENTICACIÓN --- //
export const registerUser = (data) => api.post("/auth/register", data);
export const loginUser = (data) => api.post("/auth/login", data);

// --- TRANSACCIONES --- //
export const getTransactions = (token) =>
  api.get("/transactions", { headers: { Authorization: `Bearer ${token}` } });

export const addTransaction = (transaction, token) =>
  api.post("/transactions", transaction, {
    headers: { Authorization: `Bearer ${token}` },
  });

// --- ASISTENTE IA --- //
export const askAssistant = (question, token) =>
  api.post(
    "/assistant/query",
    { question },
    { headers: { Authorization: `Bearer ${token}` } }
  );

export default api;
